def.body:

{% if Rosetta.has_constant?("DEFAULT_LOCALE") %}
      {{ Rosetta::DEFAULT_LOCALE }}
    {% else %}
      {{ Rosetta::Config::DEFAULT_LOCALE }}
    {% end %}


{% module_name = (key.split('.')).map(&.camelcase).join("::") %}



{% if Rosetta::Locales::KEYS.includes?(key) %}
      Rosetta::Locales::{{ module_name.id }}.new
    {% else %}
      {% raise("Missing translation for #{key} for all locales") %}
    {% end %}


{% if Rosetta.has_constant?("DEFAULT_LOCALE")
  default_locale = Rosetta::DEFAULT_LOCALE
else
  default_locale = Rosetta::Config::DEFAULT_LOCALE
end %}



{% if Rosetta.has_constant?("AVAILABLE_LOCALES")
  available_locales = Rosetta::AVAILABLE_LOCALES
else
  available_locales = Rosetta::Config::AVAILABLE_LOCALES
end %}



{% translations = run("./runner", path, default_locale.id, (available_locales.join(',')).id)
if !(translations.stringify.starts_with?("module Rosetta"))
  raise(translations.stringify)
end
 %}



{{ translations }}


{% if key.starts_with?('.')
  if @type.has_constant?("ROSETTA_PREFIX")
    key = "#{ROSETTA_PREFIX.id}#{key.id}"
  else
    key = "#{(@type.id.underscore.gsub(/::/, ".")).id}#{key.id}"
  end
end %}


      Rosetta.t(
{{ key }}
)
